"}, {"fieldtype": "Section Break", "label": "qr"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set inv_additional_fields_name = frappe.db.get_value(
    'Sales Invoice Additional Fields',
    {"sales_invoice": doc.name},
    "name"
) %}

{% set inv_additional_fields = 
    frappe.get_doc("Sales Invoice Additional Fields", inv_additional_fields_name) 
    if inv_additional_fields_name else None 
%}

<!-- ====== كود QR - بدون أي تباعد خارجي أو داخلي، عرض كامل، وضوح عالي للطباعة الحرارية ====== -->
<div 
  class="qr-block" 
  id="qr-block-main" 
  style="
    /* غلاف الـ QR: بدون أي تباعد أو ظل أو خلفية، عرض 100% */
    text-align:center;
    margin:0 !important;            /* لا يوجد أي تباعد قبل أو بعد البلوك */
    padding:0 !important;
    width:100%;
    max-width:100%;
    box-sizing:border-box;
    background:none !important;
  "
>
  <style>
    /* غلاف البلوك: بدون تباعد خارجي أو داخلي، عرض كامل */
    .qr-block {
        text-align: center;
        margin: 0 !important;
        padding: 0 !important;
        width: 100%;
        max-width: 100%;
        box-sizing: border-box;
        background: none !important;
    }
    /* صورة QR: وضوح عالي، بدون تباعد */
    .qr-image {
        width: 180px;
        height: 180px;
        object-fit: contain;
        margin: 0 auto !important;
        display: block;
        filter: none !important;
        background: none !important;
        padding: 0 !important;
        box-sizing: border-box;
    }
    /* رسالة الخطأ: وضوح عالي، أقل تباعد سطور */
    .qr-error {
        color: #000 !important;
        font-weight: bold;
        font-size: 12px;
        margin: 0 !important;
        padding: 0 !important;
        line-height: 0.95;
        background: none !important;
        width: 100%;
        text-align: center;
    }
    @media print {
      .qr-block,
      .qr-image,
      .qr-error {
        background: none !important;
      }
    }
