html:
{# -------------------------------------------
    القسم 1: استدعاء بيانات زاتكا (ZATCA) والحقول الإضافية
    ------------------------------------------- #}
{% set seller_name, vat_registration_no, street, city, district, postal_code,
       enable_zatca_integration, type_of_transaction 
    = frappe.db.get_value(
        "ZATCA Business Settings",
        {"company": doc.company}, 
        [
            "seller_name",
            "vat_registration_number", 
            "street", 
            "city", 
            "district", 
            "postal_code", 
            "enable_zatca_integration", 
            "type_of_business_transactions"
        ]
    )
%}

{# استدعاء الحقول الإضافية للفاتورة (Sales Invoice Additional Fields) #}
{% set inv_additional_fields_name = frappe.db.get_value(
    'Sales Invoice Additional Fields',
    {"sales_invoice": doc.name},
    "name"
) %}
{% set inv_additional_fields = 
    frappe.get_doc("Sales Invoice Additional Fields", inv_additional_fields_name) 
    if inv_additional_fields_name else None 
%}

{# إعدادات سجل تجاري البائع #}
{% set setting_id = frappe.db.get_value(
    "ZATCA Business Settings",
    {"company": doc.company},
    "name"
) %}
{% set seller_commercial_reg_no = frappe.db.get_value(
    "Additional Seller IDs",
    {"parent": setting_id, 'type_name': 'Commercial Registration Number'},
    'value'
) %}

{# الرقم التجاري للعميل إذا وجد #}
{% set customer_commercial_reg_no = frappe.db.get_value(
    "Additional Buyer IDs",
    {"parent": doc.customer, 'type_name': 'Commercial Registration Number'},
    'value'
) %}

{# القسم 2: تحديد نوع الفاتورة (Standard / Simplified) بالاعتماد على ZATCA #}
{% set details = get_phase_2_print_format_details(doc) %}
{% if details %}
    {% if (details.siaf.invoice_type_transaction)[:2] == '01' %}
        {% set invoice_type = "Standard" %}
    {% elif (details.siaf.invoice_type_transaction)[:2] == '02' %}
        {% set invoice_type = "Simplified" %}
    {% endif %}
{% else %}
    {% set invoice_type = "Simplified" %}
{% endif %}

{% if seller_name and enable_zatca_integration == 1 %}

    {% set invoice_type = 
        "Standard" if (
            inv_additional_fields
            and inv_additional_fields.invoice_type_transaction[:2] == '01'
        ) 
        else "Simplified"
    %}

    {% set dir = "rtl" if frappe["form_dict"]["_lang"] in ["العربية"] else "ltr" %}

<!DOCTYPE html>
<html lang="ar">
    <head>
        <style>
            .print-format {
                font-family: 'Arial', sans-serif;
                padding: 5mm;
                font-size: 12px;
                text-align: center;
                direction: rtl;
                margin: 0 auto;
            }

            .print-format * {
                white-space: nowrap;
            }

            .block {
                border: 1px solid #000;
                border-radius: 6px;
                margin-bottom: 10px;
                padding: 5px 10px;
                display: inline-block;
                width: 100%;
                box-sizing: border-box;
            }

            .block-title {
                font-weight: bold;
                font-size: 14px;
                margin-bottom: 3px;
                border-bottom: 1px dashed #ccc;
                padding-bottom: 3px;
            }

            .print-format table {
                width: 100%;
                border-collapse: collapse !important;
                margin: 3px 0;
            }

            .print-format th,
            .print-format td {
                text-align: center;
                padding: 2px;
                font-size: 12px;
                border: 1px solid #000;
                white-space: nowrap;
            }

            .text-right { text-align: right; }
            .text-left { text-align: left; }
            .total { font-weight: bold; }

            .qr-code {
                display: flex;
                justify-content: center;
                align-items: center;
                margin: 5px 0;
                filter: brightness(1.2) sepia(1) hue-rotate(-60deg);
            }

            .qr-code img,
            .print-format img {
                max-width: 160px;
                max-height: 160px;
                height: auto;
                white-space: nowrap;
            }

            .footer {
                position: absolute;
                bottom: 5px;
                width: 100%;
                text-align: center;
            }
        </style>
    </head>
    <body>
        <div class="print-format">
            <div class="block">
                <div class="block-title">{{ invoice_type == 'Simplified' and 'فاتورة ضريبية مبسطة' or 'فاتورة ضريبية قياسية' }}</div>
                <img src="{{ frappe.db.get_value('Company', doc.company, 'company_logo') or '' }}" alt="Logo" style="max-width: 100px; margin-bottom: 5px;">
                <p><b>{{ doc.company }}</b></p>
                <p><b>{{ _("الرقم الضريبي :") }}</b> <b>{{ _("TAX ID :") }}</b> {{ frappe.db.get_value("Company", doc.company, "tax_id") or "غير متوفر" }}</p>
                <p>{{ _("الفرع") }}: {{ doc.branch or "Main" }}</p>
            </div>

            <!-- البلوك التالي للتاريخ والعميل -->
            <div class="block">
                <div class="block-title">بيانات الفاتورة</div>
                <p>{{ _("التاريخ") }}: {{ doc.get_formatted("posting_time") }} - {{ doc.get_formatted("posting_date") }}</p>
                <p>{{ _("رقم الفاتورة") }}: {{ doc.name }}</p>
                <p>{{ _("العميل") }}: {{ doc.customer_name }}</p>
            </div>

            <!-- البلوك التالي للجدول -->
            <div class="block">
                <div class="block-title">تفاصيل الأصناف</div>
                <table>
                    <thead>
                        <tr>
                            <th>{{ _("الصنف") }}</th>
                            <th>{{ _("الكمية") }}</th>
                            <th>{{ _("السعر") }}</th>
                            <th>{{ _("الإجمالي") }}</th>
                        </tr>
                    </thead>
                    <tbody>
                        {% for item in doc.items %}
                        <tr>
                            <td>{{ item.item_name }}</td>
                            <td>{{ item.qty }}</td>
                            <td>{{ item.get_formatted("rate") }}</td>
                            <td>{{ item.get_formatted("amount") }}</td>
                        </tr>
                        {% endfor %}
                    </tbody>
                </table>
            </div>

            <!-- المبالغ -->
            <div class="block">
                <div class="block-title">تفاصيل المبالغ</div>
                <table>
                    <tr><td class="text-right">{{ _("الخصم") }}</td><td>{{ doc.get_formatted("discount_amount") or "0.00" }}</td><td class="text-left">{{ _(".Discount") }}</td></tr>
                    <tr><td class="text-right">{{ _("الإجمالي الصافي") }}</td><td>{{ "{:.2f}".format(doc.net_total) }}</td><td class="text-left">{{ _(".Net Total") }}</td></tr>
                    <tr><td class="text-right">{{ _("الضريبة (15%)") }}</td><td>{{ "{:.2f}".format(doc.total_taxes_and_charges) }}</td><td class="text-left">{{ _(".VAT (15%)") }}</td></tr>
                    <tr class="total"><td class="text-right">{{ _("الإجمالي الكلي") }}</td><td>{{ doc.get_formatted("grand_total") }}</td><td class="text-left">{{ _(".Grand Total") }}</td></tr>
                    <tr class="total"><td class="text-right">{{ _("المبلغ المدفوع") }}</td><td>{{ doc.get_formatted("paid_amount") }}</td><td class="text-left">{{ _(".Paid") }}</td></tr>
                    <tr class="total"><td class="text-right">{{ _("المبلغ المتبقي") }}</td><td>{{ doc.get_formatted("change_amount") }}</td><td class="text-left">{{ _(".Remain") }}</td></tr>
                </table>
                <p style="text-align: center; font-weight: bold; font-size: 10px; margin-top: 5px;">
                    {{ frappe.utils.money_in_words(doc.grand_total, doc.currency)}}
                </p>
            </div>

            <!-- الشكر وQR -->
            <div class="block">
                <div class="block-title">شكراً لتعاملكم معنا</div>
                <p>{{ _("نقدم خدمات تجهيز الحفلات والمناسبات") }}</p>
                <p>{{ _("البضاعة المباعة لا تُرد ولا تُستبدل") }}</p>
                <p>{{ _("للتواصل جوال 0564716664") }}</p>
                <p>{{ _("واتس اب 0125666555") }}</p>
            </div>

            <div class="qr-code">
                {% if inv_additional_fields and inv_additional_fields.qr_image_src %}
                    <img src="{{ inv_additional_fields.qr_image_src }}">
                {% else %}
                    <p class="h2 text-danger">{{ _("Error : No Qr code") }}</p>
                {% endif %}
            </div>
        </div>
    </body>
{% else %}
    <p class="h2 text-danger">{{ _("Error: No active ZATCA settings") }}</p>
{% endif %}
</html>
