    <!-- 🏷️ قسم ترجمة الحالات -->
    {% set status_translation = {
        "Draft": "مسودة",
        "On Hold": "قيد الانتظار",
        "To Deliver and Bill": "للتسليم والفوترة",
        "To Bill": "للفوترة",
        "To Deliver": "للتسليم",
        "Completed": "مكتمل",
        "Cancelled": "ملغي",
        "Closed": "مغلق"
    } %}

