    {% set status_classes = {
        "Completed": "stamp is-completed",
        "Cancelled": "stamp is-cancelled",
        "Closed": "stamp is-closed",
        "Draft": "stamp is-draft",
        "On Hold": "stamp is-on-hold",
        "To Deliver and Bill": "stamp is-to-deliver-bill",
        "To Bill": "stamp is-to-bill",
        "To Deliver": "stamp is-to-deliver"
    } %}

    {% set translated_status = status_translation.get(doc.status, doc.status) %}
    {% set status_class = status_classes.get(doc.status, "stamp") %}

    <!-- 🏷️ عرض الختم -->
    <div style="display: flex; justify-content: center; align-items: center; margin-top: 15px;">
        <span class="{{ status_class }}">{{ translated_status }}</span>
    </div>

    <!-- 🎨 الأسلوب الأساسي للختم مع تصغير الحجم -->
    <style>
        .stamp {
            font-size: 1.5rem; /* ✅ حجم أصغر */
            font-weight: 600;
            border: 0.2rem double;
            padding: 0.3rem 0.8rem;
            text-transform: uppercase;
            border-radius: 0.5rem;
            font-family: 'Courier';
            mix-blend-mode: multiply;
            display: inline-block;
            transform: rotate(-5deg); /* ✅ ميل بسيط للختم */
