[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<!-- ====== هيدر الفاتورة - كل عنصر في سطر ====== -->
<div
  style="
    width: 100%;
    max-width: 100%;
    margin: 1px 0;
    padding: 0;
    box-sizing: border-box;
    background: none;
  "
>
  <div
    style="
      width: 100%;
      max-width: 500px;
      margin: 0 auto;
      direction: rtl;
      text-align: center;
      font-family: Arial, sans-serif;
      padding: 0;
      background: none;
    "
  >
    <!-- عنوان الفاتورة -->
    <p
      style="
        font-size: 18px;
        font-weight: bold;
        margin: 0 0 4px 0;
        color: #000;
        white-space: nowrap;
        font-family: Cairo;
        padding: 0;
        background: none;
      "
    >
      {% if doc.is_return == 1 %} مرتجع فاتورة ضريبية مبسطه {% else %} فاتورة
      ضريبية مبسطة {% endif %}
    </p>

    <!-- شعار الشركة -->
    <img
      src="{{ frappe.db.get_value('Company', doc.company, 'company_logo') or '' }}"
      alt="Logo"
      style="
        display: block;
        width: 120px;
        height: auto;
        margin: 0 auto 4px;
        padding: 0;
        background: none;
        object-fit: contain;
      "
    />

    <!-- معلومات الشركة -->
    <div
      style="
        width: 100%;
        text-align: right;
        margin: 0;
        padding: 0;
        background: none;
      "
    >
      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >اسم الشركة :</span
        >
        <div
          style="
            margin: 0;
            padding: 0;
            font-size: 13px;
            line-height: 1.2;
            white-space: normal;
            display: inline-block;
            vertical-align: top;
          "
        >
          <span style="color: #000">شركة مذاق الأندلس للحلويات</span><br />
          <span style="color: #000">شركة شخص واحد</span>
        </div>
      </div>

      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >الرقم الضريبي :</span
        >
        <span style="color: #000; white-space: normal; padding-left: 2px"
          >311073268600003</span
        >
      </div>

      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >الفرع :</span
        >
        <span style="color: #000; white-space: normal; padding-left: 2px"
          >{{ doc.branch or "الرئيسي" }}</span
        >
      </div>

      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >السجل التجاري :</span
        >
        <span style="color: #000; white-space: normal; padding-left: 2px"
          >{{ doc.branch_cr_no or "غير متوفر" }}</span
        >
      </div>

      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >رقم الفاتورة :</span
        >
        <span style="color: #000; white-space: normal; padding-left: 2px"
          >{{ doc.name }}</span
        >
      </div>

      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >التاريخ :</span
        >
        <span style="color: #000; white-space: normal; padding-left: 2px">
          {% set days_ar =
          ['الاثنين','الثلاثاء','الأربعاء','الخميس','الجمعة','السبت','الأحد'] %}
          {% set dt = frappe.utils.get_datetime(doc.posting_date ~ ' ' ~
          doc.posting_time) %} {% set weekday = dt.weekday() %} {% set hour =
          dt.hour %} {% set minute = dt.minute %} {% set hour12 = hour if 1 <=
          hour <= 12 else (hour - 12 if hour > 12 else 12) %} {% set am_pm =
          'صباحا' if hour < 12 else 'مساءا' %} {{ days_ar[weekday] }} {{ dt.day
          }}-{{ dt.month }}-{{ dt.year }}
        </span>
      </div>

      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >الساعة :</span
        >
        <span style="color: #000; white-space: normal; padding-left: 2px">
          {{ hour12 }}:{{ "%02d"|format(minute) }} {{ am_pm }}
        </span>
      </div>

      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >العميل :</span
        >
        <span style="color: #000; white-space: normal; padding-left: 2px"
          >{{ doc.customer_name }}</span
        >
      </div>

      {% if doc.customer_cr_no %}
      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >السجل التجاري للعميل :</span
        >
        <span style="color: #000; white-space: normal; padding-left: 2px"
          >{{ doc.customer_cr_no }}</span
        >
      </div>
      {% endif %} {% if doc.customer_700_number %}
      <div style="margin: 0; padding: 0; font-size: 13px; line-height: 1.2">
        <span style="font-weight: bold; color: #000; white-space: nowrap"
          >الرقم الموحد للمنشأة :</span
        >
        <span style="color: #000; white-space: normal; padding-left: 2px"
          >{{ doc.customer_700_number }}</span
        >
      </div>
      {% endif %}
    </div>
  </div>
</div>
